mapper.update:
update orders
status = #{status}, checkout_time = #{checkoutTime}, pay_method = #{payMethod}, pay_status = #{payStatus}, cancel_time = #{cancelTime}, cancel_reason = #{cancelReason}, rejection_reason = #{rejectionReason},
where number = #{number}

mapper.select:
select * from orders

    status = #{status}
    and number = #{number}
    and phone = #{phone}
    and order_time >= #{beginTime}
    and order_time <= #{endTime}

order by number desc
